import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaChevronLeft, FaChevronRight, FaChevronUp, FaChevronDown, FaEdit, FaTimes, FaTrash } from 'react-icons/fa';
import PlayerViewPage from './PlayerViewPage';
//import './GMViewPage.css';


const GMViewPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [profiles, setProfiles] = useState([]);
  const [selectedProfileId, setSelectedProfileId] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [profilesPerPage, setProfilesPerPage] = useState(5);
  const [gameLog, setGameLog] = useState({ discoveries: [], battles: [], notes: [] });
  const [expandedSections, setExpandedSections] = useState({ discoveries: false, battles: false, notes: false });
  const [isEditing, setIsEditing] = useState(false);
  const [newEntries, setNewEntries] = useState({ discoveries: "", battles: "", notes: "" });

  const [editingItem, setEditingItem] = useState(null);
  const [editedValue, setEditedValue] = useState("");



  useEffect(() => {
    const fetchProfiles = async () => {
      try {
        const url = `http://localhost:5001/campaigns/${id}/profiles`;  // Ensure this is correct
        const response = await fetch(url, { headers: { 'Cache-Control': 'no-cache' } });
        const data = await response.json();
        setProfiles(data.profiles);
      } catch (error) {
        console.error('Error fetching profiles:', error);
      }
    };

    const fetchGameLog = async () => {
      try {
        const url = `http://localhost:5001/campaigns/${id}/gamelog`;
        const response = await fetch(url, { headers: { 'Cache-Control': 'no-cache' } });
        const data = await response.json();
        setGameLog(data);
      } catch (error) {
        console.error('Error fetching game log:', error);
      }
    };

    fetchProfiles();
    fetchGameLog();

    const updateProfilesPerPage = () => {
      const cardWidth = 180 + 20; // profile-card width + gap
      const availableWidth = window.innerWidth - 120; // account for padding or arrows
      const count = Math.floor(availableWidth / cardWidth); // Number of profiles that fit within the screen width
      setProfilesPerPage(Math.max(1, count) - 1); // Ensure at least one profile shows
    };

    updateProfilesPerPage();
    window.addEventListener('resize', updateProfilesPerPage);

    return () => window.removeEventListener('resize', updateProfilesPerPage);

  }, [id]);

  const handleProfileClick = (profileId) => {
    setSelectedProfileId(profileId); // Set the selected profile ID when a profile name is clicked
  };

  const handleNext = () => {
    if (currentIndex + profilesPerPage < profiles.length) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrev = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const handleBackToCampaigns = () => {
    navigate('/');
  };

  const toggleSection = (section) => {
    setExpandedSections((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  const toggleEditMode = () => {
    setIsEditing(!isEditing);
  };

  const handleAddItem = async (section) => {
    if (newEntries[section].trim()) {
      const updatedGameLog = {
        ...gameLog,
        [section]: [...gameLog[section], newEntries[section].trim()],
      };

      setGameLog(updatedGameLog); // Update UI

      // Send updated data to backend
      try {
        await fetch(`http://localhost:5001/campaigns/${id}/gamelog`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedGameLog),
        });
      } catch (error) {
        console.error('Error updating game log:', error);
      }

      setNewEntries((prev) => ({ ...prev, [section]: "" }));
    }
  };

  const handleDeleteItem = async (section, index) => {
    const updatedItems = gameLog[section].filter((_, i) => i !== index);
    const updatedGameLog = { ...gameLog, [section]: updatedItems };

    setGameLog(updatedGameLog); // Update UI

    // Send updated data to backend
    try {
      await fetch(`http://localhost:5001/campaigns/${id}/gamelog`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedGameLog),
      });
    } catch (error) {
      console.error('Error updating game log:', error);
    }
  };


  const handleEditItem = async (section, index, item) => {
    if (editingItem === index) {
      // Save changes to the entry
      const updatedItems = gameLog[section].map((currentItem, i) =>
        i === index ? editedValue : currentItem
      );
      const updatedGameLog = { ...gameLog, [section]: updatedItems };

      // Update the game log state (UI update)
      setGameLog(updatedGameLog);
      setEditingItem(null); // Stop editing

      // Send the updated game log to the backend
      try {
        await fetch(`http://localhost:5001/campaigns/${id}/gamelog`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedGameLog),
        });
      } catch (error) {
        console.error('Error updating game log:', error);
      }
    } else {
      // Start editing this entry
      setEditingItem(index);
      setEditedValue(item); // Set the value of the entry to the current item
    }
  };

  return (
    <div className="gm-view-page">
      <div className="gm-view-container">
        {/* Only show the profile carousel if no profile is selected */}
        {!selectedProfileId && (
          <div className="profile-carousel">
            <button className="arrow" onClick={handlePrev} disabled={currentIndex === 0}>
              <FaChevronLeft />
            </button>
            <div className="profile-list">
              {profiles.slice(currentIndex, currentIndex + profilesPerPage).map((profile) => (
                <div key={profile.id} className="profile-card" onClick={() => handleProfileClick(profile.id)}>
                  <p>{profile.name}</p>
                </div>
              ))}
            </div>
            <button className="arrow" onClick={handleNext} disabled={currentIndex + profilesPerPage >= profiles.length}>
              <FaChevronRight />
            </button>
          </div>
        )}

        {/* Render PlayerViewPage when a profile is selected */}
        {selectedProfileId && (
          <div>
            <PlayerViewPage campaignId={id} profileId={selectedProfileId} />
            <button onClick={() => setSelectedProfileId(null)} className="return-to-gm-button">
              Return to GM View
            </button>
          </div>
        )}

        <div className="game-log">
          <h1>Game Log</h1>
          {['discoveries', 'battles', 'notes'].map((section) => (
            <div key={section} className="game-log-section">
              {/* This div now only wraps the heading */}
              <div className="game-log-section-header">
                <h3 onClick={() => toggleSection(section)} style={{ cursor: 'pointer' }}>
                  {section.charAt(0).toUpperCase() + section.slice(1)}
                  {expandedSections[section] ? <FaChevronUp /> : <FaChevronDown />}
                </h3>
              </div>

              {/* The list items are now in a separate container */}
              {expandedSections[section] && (
                <div className="game-log-items">
                  <ul>
                    {gameLog[section].map((item, index) => (
                      <li key={index}>
                        {/* If the item is being edited, show the textarea */}
                        {editingItem === index ? (
                          <textarea
                            value={editedValue}
                            onChange={(e) => setEditedValue(e.target.value)}
                            rows="4"
                            cols="50"
                          />
                        ) : (
                          <div>
                            {item.split('\n').map((paragraph, i) => <p key={i}>{paragraph}</p>)}
                          </div>
                        )}

                        {/* Only show the delete/save button if we are in edit mode */}
                        {isEditing && (
                          <div>
                            {editingItem === index ? (
                              <button className="save" onClick={() => handleEditItem(section, index, item)}>
                                Save
                              </button>
                            ) : (
                              <button className="delete" onClick={() => handleDeleteItem(section, index)}>
                                <FaTrash /> Delete
                              </button>
                            )}
                          </div>
                        )}
                      </li>
                    ))}
                  </ul>

                  {/* Only show the textarea and add button if in edit mode */}
                  {isEditing && (
                    <>
                      <textarea
                        value={newEntries[section]}
                        onChange={(e) => setNewEntries((prev) => ({ ...prev, [section]: e.target.value }))}
                        rows="4"
                        cols="50"
                        placeholder={`Enter new ${section.slice(0, -1)}`}
                      />
                      <button onClick={() => handleAddItem(section)}>Add {section === 'discoveries' ? 'Discovery' : section.slice(0, -1)}</button>
                    </>
                  )}
                </div>
              )}



            </div>
          ))}

        </div>

        <div className="buttons" style={{ position: 'relative', height: '80px' }}>
          {isEditing ? (
            <button onClick={toggleEditMode} className="exit-edit-button">
              <FaTimes /> Exit Edit Mode
            </button>
          ) : (
            <button onClick={toggleEditMode} className="edit-view">
              <FaEdit /> Edit Game Log
            </button>
          )}

          <button onClick={handleBackToCampaigns} className="back-to-campaigns-button" style={{ position: 'relative', height: '50px' }}>
            Back to Campaigns
          </button>
        </div>



      </div>
    </div>
  );
};

export default GMViewPage;
