import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [campaignName, setCampaignName] = useState('');
  const [selectedCampaign, setSelectedCampaign] = useState(null);
  const [editMode, setEditMode] = useState(false); // Track if we are in the Edit view

  useEffect(() => {
    // Fetch campaigns when the component mounts
    axios.get('http://localhost:5001/campaigns').then(response => {
      setCampaigns(response.data);
    });
  }, []);

  const addCampaign = async () => {
    const response = await axios.post('http://localhost:5001/campaigns', { name: campaignName });
    setCampaigns([...campaigns, response.data]);
    setCampaignName('');
  };

  const deleteCampaign = async (id) => {
    await axios.delete(`http://localhost:5001/campaigns/${id}`);
    setCampaigns(campaigns.filter(campaign => campaign.id !== id));
  };

  const handleCampaignClick = (id) => {
    if (editMode) {
      // Allow campaign name change only if we're in edit mode
      const newName = prompt('Enter new campaign name:', campaigns.find(campaign => campaign.id === id).name);
      if (newName) {
        updateCampaignName(id, newName);
      }
    }
  };

  const updateCampaignName = async (id, newName) => {
    const response = await axios.put(`http://localhost:5001/campaigns/${id}`, { name: newName });
    setCampaigns(campaigns.map(campaign => campaign.id === id ? response.data : campaign));
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl mb-4">Digital Score & Stat Keeper</h1>

      {/* Edit Mode Toggle Button */}
      <div className="flex justify-between mb-4">
        <h2 className="text-2xl">Campaigns</h2>
        <button
          onClick={() => setEditMode(!editMode)}
          className="p-2 bg-blue-500 text-white rounded"
        >
          {editMode ? 'Exit' : 'Edit'}
        </button>
      </div>

      {editMode ? (
        <div>
          {/* Add Campaign */}
          <input
            type="text"
            value={campaignName}
            onChange={(e) => setCampaignName(e.target.value)}
            className="border p-2 rounded mb-4"
            placeholder="Enter Campaign Name"
          />
          <button onClick={addCampaign} className="p-2 bg-green-500 text-white rounded">
            Add New Campaign
          </button>

          {/* List of Campaigns with Delete Option */}
          <ul className="mt-4">
            {campaigns.map(campaign => (
              <li key={campaign.id} className="flex justify-between items-center mb-2">
                <span
                  onClick={() => handleCampaignClick(campaign.id)}
                  className="cursor-pointer text-blue-600"
                >
                  {campaign.name}
                </span>
                <button
                  onClick={() => deleteCampaign(campaign.id)}
                  className="ml-2 p-1 bg-red-500 text-white rounded"
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          {/* Default View - List Campaigns */}
          <ul>
            {campaigns.map(campaign => (
              <li key={campaign.id} className="mb-2">
                <span
                  onClick={() => handleCampaignClick(campaign.id)}
                  className="cursor-pointer text-blue-600"
                >
                  {campaign.name}
                </span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
