import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ProfilePage = () => {
  const { campaignId } = useParams();
  const navigate = useNavigate();
  const [profiles, setProfiles] = useState([]);
  const [profileName, setProfileName] = useState('');
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5001/profiles/${campaignId}`).then(response => {
      setProfiles(response.data);
    });
  }, [campaignId]);

  const addProfile = async () => {
    const response = await axios.post('http://localhost:5001/profiles', {
      campaign_id: campaignId,
      name: profileName,
      stats: 'Stats here',
      equipment: 'Equipment here',
      skills: 'Skills here',
    });
    setProfiles([...profiles, response.data]);
    setProfileName('');
  };

  const deleteProfile = async (id) => {
    await axios.delete(`http://localhost:5001/profiles/${id}`);
    setProfiles(profiles.filter(profile => profile.id !== id));
  };

  const handleProfileClick = (id) => {
    if (editMode) {
      const newName = prompt('Enter new profile name:', profiles.find(profile => profile.id === id).name);
      if (newName) {
        updateProfileName(id, newName);
      }
    }
  };

  const updateProfileName = async (id, newName) => {
    const response = await axios.put(`http://localhost:5001/profiles/${id}`, { name: newName });
    setProfiles(profiles.map(profile => profile.id === id ? response.data : profile));
  };

  return (
    <div className="p-8">
      <h1 className="text-3xl mb-4">Campaign Profiles</h1>

      {/* Edit Mode Toggle */}
      <div className="flex justify-between mb-4">
        <h2 className="text-2xl">Profiles</h2>
        <button
          onClick={() => setEditMode(!editMode)}
          className="p-2 bg-blue-500 text-white rounded"
        >
          {editMode ? 'Exit' : 'Edit'}
        </button>
      </div>

      {editMode ? (
        <div>
          {/* Add Profile */}
          <input
            type="text"
            value={profileName}
            onChange={(e) => setProfileName(e.target.value)}
            className="border p-2 rounded mb-4"
            placeholder="Enter Profile Name"
          />
          <button onClick={addProfile} className="p-2 bg-green-500 text-white rounded">
            Add New Profile
          </button>

          {/* List of Profiles with Delete Option */}
          <ul className="mt-4">
            {profiles.map(profile => (
              <li key={profile.id} className="flex justify-between items-center mb-2">
                <span
                  onClick={() => handleProfileClick(profile.id)}
                  className="cursor-pointer text-blue-600"
                >
                  {profile.name}
                </span>
                <button
                  onClick={() => deleteProfile(profile.id)}
                  className="ml-2 p-1 bg-red-500 text-white rounded"
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div>
          {/* Default View - List Profiles */}
          <ul>
            {profiles.map(profile => (
              <li key={profile.id} className="mb-2">
                <span
                  onClick={() => handleProfileClick(profile.id)}
                  className="cursor-pointer text-blue-600"
                >
                  {profile.name}
                </span>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Back to Campaign List */}
      <button
        onClick={() => navigate('/')}
        className="mt-4 p-2 bg-gray-500 text-white rounded"
      >
        Back to Campaigns
      </button>
    </div>
  );
};

export default ProfilePage;
